truncate IdDlyaSmsModulirovaniya;
insert into IdDlyaSmsModulirovaniya (id)


select
	ds.r_debt_msg_id
	----datepart(day, ds.send_date) 'День месяца'
	--,count(ds.id) 'Кол-во отправленных смс'
	--,len(ds.text) 'Кол-во символов'
	--,p.number 'Телефон'
	--,ds.parent_id
	,avg(len(ds.text)) / 67
from
	debt_sms ds
	left join phone p on ds.r_phone_id = p.id

where
	ds.r_debt_msg_id in (29, 122, 112, 113, 121, 97)
	and ds.send_date between '01-04-2016' and '30-04-2016'

group by
	
	ds.r_debt_msg_id
	----,datepart(day, ds.send_date)
	--,ds.text
	--,p.number
	--,ds.parent_id

------------------------------------------------------------------------


--телефонов по этим долгам
select distinct
	sum(ph.kolvo)

from
	[i_collect].[dbo].[bank] as b
	inner join [i_collect].[dbo].[portfolio] as p on b.id = p.parent_id
	inner join [i_collect].[dbo].[debt] as d on p.id = d.r_portfolio_id
	inner join [i_collect].[dbo].[person] as per on d.parent_id=per.id
	--inner join IdDlyaSmsModulirovaniya i on d.id = i.id

	inner join
			(
			select
				p.parent_id
				,count(p.number) kolvo
			from
				phone p
			

				
			
			where
				p.typ = 1
				and p.block_flag = 0
				and len(p.number2) = 11
				and p.id not in
								(
								select
									s.r_phone_id
								from
									debt_sms s
								where
									s.send_date between '01-04-2016' and '01-04-2016'
									and s.status != 2
								)
			group by
				p.parent_id

			)ph
				on per.id = ph.parent_id

where
	d.id in
